BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "amendementen" (
	"id"	INTEGER NOT NULL,
	"titel"	VARCHAR(200) NOT NULL,
	"constaterende_dat"	TEXT,
	"overwegende_dat"	TEXT,
	"opdracht_formulering"	TEXT NOT NULL,
	"wijzigingen"	TEXT,
	"status"	VARCHAR(20),
	"gemeenteraad_datum"	VARCHAR(40),
	"created_at"	DATETIME,
	"updated_at"	DATETIME,
	"created_by"	INTEGER NOT NULL,
	"agendapunt"	VARCHAR(40),
	PRIMARY KEY("id"),
	FOREIGN KEY("created_by") REFERENCES "user"("id")
);
CREATE TABLE IF NOT EXISTS "motie" (
	"id"	INTEGER NOT NULL,
	"titel"	VARCHAR(200) NOT NULL,
	"constaterende_dat"	TEXT,
	"overwegende_dat"	TEXT,
	"draagt_college_op"	TEXT,
	"status"	VARCHAR(20),
	"created_at"	DATETIME,
	"updated_at"	DATETIME,
	"created_by"	INTEGER NOT NULL,
	"opdracht_formulering"	TEXT NOT NULL,
	"gemeenteraad_datum"	VARCHAR(40),
	"agendapunt"	VARCHAR(40),
	PRIMARY KEY("id"),
	FOREIGN KEY("created_by") REFERENCES "user"("id")
);
CREATE TABLE IF NOT EXISTS "motie_medeindieners" (
	"motie_id"	INTEGER NOT NULL,
	"user_id"	INTEGER NOT NULL,
	PRIMARY KEY("motie_id","user_id"),
	CONSTRAINT "uq_motie_user" UNIQUE("motie_id","user_id"),
	FOREIGN KEY("motie_id") REFERENCES "motie"("id") ON DELETE CASCADE,
	FOREIGN KEY("user_id") REFERENCES "user"("id") ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS "party" (
	"id"	INTEGER NOT NULL,
	"naam"	VARCHAR(100) NOT NULL,
	"afkorting"	VARCHAR(10) NOT NULL,
	"actief"	BOOLEAN,
	"created_at"	DATETIME,
	"logo_url"	VARCHAR(512),
	"logo_filename"	VARCHAR(255),
	UNIQUE("afkorting"),
	PRIMARY KEY("id"),
	UNIQUE("naam")
);
CREATE TABLE IF NOT EXISTS "user" (
	"id"	INTEGER NOT NULL,
	"email"	VARCHAR(120) NOT NULL,
	"password_hash"	VARCHAR(120) NOT NULL,
	"naam"	VARCHAR(100) NOT NULL,
	"partij_id"	INTEGER,
	"created_at"	DATETIME,
	"role"	VARCHAR(120),
	"profile_url"	VARCHAR(512),
	"profile_filename"	VARCHAR(255),
	UNIQUE("email"),
	PRIMARY KEY("id"),
	FOREIGN KEY("partij_id") REFERENCES "party"("id")
);
INSERT INTO "amendementen" VALUES (1,'awefasdf','["asdfasdfas", "dfasdfasdf", "asdfasdfasdfasdf", "asdfasdfasdfas", "dfasdfasdfasdfasdfasdf"]','["asdfasdfasdfasdfas", "dfasdfasdfasdfasdfasdfasdfasdfasdf", "asdfasdfasdf", "asdfasdfasdf"]','asdfasdfasdfasdfasdfasd','["fasdfasdfasdfasdfasdfasdfasdf", "asdfasdfasdfasdfasdfasdfasd", "fasdfasdfasdfasdfasdfasdfasdfasdfasdfa"]','GereedAdvies','Gemeenteraad 27 oktober 2025','2025-09-23 11:57:48.501466','2025-09-23 11:57:48.501471',1,'2');
INSERT INTO "motie" VALUES (1,'Motie: Fietsveilig oversteken bij Schalkwijkerallee','["dat op de Schalkwijkerallee regelmatig bijna-ongevallen plaatsvinden","dat scholen in de omgeving liggen waardoor veel kinderen de oversteek maken","dat belijning en markering op meerdere plekken zijn vervaagd","dat bewoners herhaaldelijk melding hebben gedaan via Fixi","dat de wijkraad heeft verzocht om snelle maatregelen"]','["overwegende dat verkeersveiligheid voor kwetsbare groepen prioriteit heeft","overwegende dat kleine, snelle ingrepen grote impact kunnen hebben","overwegende dat de kosten beperkt kunnen blijven bij onderhoudsmaatregelen","overwegende dat draagvlak in de wijk aantoonbaar aanwezig is","overwegende dat monitoring nodig is om effect te meten"]','["binnen zes weken een plan van aanpak op te leveren","de oversteekpunten opnieuw te belijnen en te voorzien van reflectie","in de ochtendspits een tijdelijke verkeersregelaar in te zetten tijdens de pilot","de wijkraad en scholen actief te betrekken bij de uitwerking","na drie maanden de effecten te evalueren en de raad te informeren"]','concept','2021-02-12 10:00:00','2021-02-12 10:00:00',7,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (2,'Motie: Snellere afhandeling meldingen illegale dump in de Waarderpolder','["dat in de Waarderpolder geregeld afval illegaal wordt gedumpt","dat dit onveilig en onhygiënisch is voor werknemers en bezoekers","dat de huidige doorlooptijd van verwijdering oploopt tot meerdere dagen","dat ondernemers bereid zijn om mee te denken over oplossingen","dat camerabewaking op enkele hotspots ontbreekt"]','["overwegende dat handhaving zichtbaar en voorspelbaar moet zijn","overwegende dat samenwerking met ondernemers de naleving kan vergroten","overwegende dat betere data-inzet hotspots inzichtelijk maakt","overwegende dat hersteltijd van de openbare ruimte de beleving sterk beïnvloedt","overwegende dat kosten deels kunnen worden gedekt uit afvalstoffenheffing"]','["dat de aanpak van illegale dump prioriteit heeft","dat data-gestuurde handhaving gewenst is","dat ondernemersstructureel worden betrokken","dat resultaten publiek gedeeld moeten worden","dat de raad halfjaarlijks de voortgang wil ontvangen"]','ingediend','2021-04-08 09:30:00','2021-04-08 09:30:00',13,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (3,'Motie: Versnellen woningbouw op transformatielocaties Oud Schoten','["dat de woningnood in Haarlem onverminderd hoog is","dat meerdere panden in Oud Schoten geschikt zijn voor transformatie","dat procedures en vergunningen veel tijd vergen","dat starters en middeninkomens in de knel zitten","dat de provincie ruimte biedt binnen bestaande kaders"]','["overwegende dat versnelling vereist is om uitstroom te bevorderen","overwegende dat tijdelijke woonvormen een brug kunnen slaan","overwegende dat gemeentelijke regie nodig is op prioritaire locaties","overwegende dat participatie de kwaliteit en acceptatie vergroot","overwegende dat financiële kaders bewaakt moeten worden"]','["een versnellingsagenda voor drie transformatielocaties op te stellen","een taskforce vergunningen in te richten voor deze locaties","mogelijkheden voor tijdelijke woningen te verkennen","de omgeving vroegtijdig te betrekken via een participatieplan","elk kwartaal voortgang te rapporteren aan de raad"]','aangenomen','2021-09-21 14:15:00','2021-09-21 14:15:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (4,'Motie: Toegankelijke gemeentelijke communicatie in B1-niveau','["dat inwoners gemeentelijke brieven en webteksten soms moeilijk begrijpen","dat B1-taalgebruik landelijk wordt aanbevolen","dat taalarmoede leidt tot miscommunicatie en gemiste rechten","dat de webtoegankelijkheidsnormen WCAG 2.2 gelden","dat de gemeente inzet op begrijpelijke dienstverlening"]','["overwegende dat heldere taal de toegankelijkheid vergroot","overwegende dat begrijpelijke communicatie fouten en bezwaren reduceert","overwegende dat het vertrouwen in de overheid hiermee toeneemt","overwegende dat training medewerkers vereist","overwegende dat monitoring nodig is om naleving te borgen"]','["dat begrijpelijke taal de norm is in Haarlem","dat alle publieksbrieven naar B1 worden herschreven","dat medewerkers trainingen krijgen","dat de website periodiek op leesbaarheid wordt getoetst","dat de raad jaarlijks een rapportage ontvangt"]','verworpen','2021-11-30 19:45:00','2021-11-30 19:45:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (5,'Motie: Aanpak hittestress in stenige straten Leidsebuurt','["dat zomerse hittegolven vaker voorkomen","dat stenige straten in de Leidsebuurt weinig schaduw bieden","dat ouderen en jonge kinderen extra kwetsbaar zijn","dat bewoners groene initiatieven willen starten","dat hemelwater onvoldoende wordt geborgen"]','["overwegende dat vergroening gezondheidswinst oplevert","overwegende dat wadi’s piekbuien helpen opvangen","overwegende dat bewonersinitiatieven versterkt kunnen worden met subsidies","overwegende dat koppelkansen met onderhoud bestaan","overwegende dat monitoring nodig is op temperatuur en water"]','["twee koelte-routes te ontwerpen met schaduw en zitplekken","1.000 m2 tegels te vervangen door groen","ondersteuningsregeling voor geveltuintjes te verruimen","deze ingrepen te koppelen aan gepland straatonderhoud","na de zomer de resultaten te evalueren met bewoners"]','concept','2022-01-18 08:10:00','2022-01-18 08:10:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (6,'Motie: Verbeteren jeugdhulp door wachttijden-dashboard','["dat wachttijden in de jeugdhulp oplopen","dat ouders en jongeren onduidelijkheid ervaren","dat aanbieders data verschillend aanleveren","dat sturing zonder eenduidige informatie lastig is","dat de raad behoefte heeft aan inzicht in trends"]','["overwegende dat transparantie wachtlijsten vermindert","overwegende dat één format voor aanbieders nodig is","overwegende dat privacy in acht moet worden genomen","overwegende dat periodieke sturing zorgt voor bijsturing","overwegende dat samenwerking in de regio kansen biedt"]','["dat een publiek wachttijden-dashboard wenselijk is","dat aanbieders in één format rapporteren","dat privacy-by-design randvoorwaarde is","dat kwartaalrapportages aan de raad worden gedeeld","dat de effectiviteit na een jaar wordt geëvalueerd"]','ingediend','2022-03-07 16:20:00','2022-03-07 16:20:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (7,'Motie: Nachtelijke geluidsoverlast horeca binnenstad terugdringen','["dat omwonenden structurele hinder ervaren na sluitingstijden","dat huidige afspraken onvoldoende worden nageleefd","dat handhaving in piekuren onder druk staat","dat ondernemers ook hinder van overlast hebben","dat bezoekersstromen zich concentreren in smalle straten"]','["overwegende dat leefbaarheid en levendigheid in balans moeten zijn","overwegende dat gerichte, zichtbare handhaving nodig is","overwegende dat afspraken met ondernemers aangescherpt moeten worden","overwegende dat beter spreiden uitstroom helpt","overwegende dat communicatie naar bezoekers effect heeft"]','["een uitgaansconvenant 2.0 met heldere normen te sluiten","handhavingscapaciteit in pieknachten tijdelijk te verhogen","uitstroomroutes te sturen met hosts en signalering","een meldpunt-nacht met terugkoppeling in te richten","na zes maanden de resultaten te delen"]','aangenomen','2022-05-24 11:55:00','2022-05-24 11:55:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (8,'Motie: Digitale inclusie – hulp bij loket en bibliotheek','["dat 1 op de 5 inwoners moeite heeft met digitale vaardigheden","dat steeds meer diensten digitaal worden aangeboden","dat de bibliotheek al ondersteuning biedt maar capaciteit beperkt is","dat kwetsbare groepen hierdoor hulp missen","dat maatschappelijke organisaties willen samenwerken"]','["overwegende dat toegang tot dienstverlening gelijk moet zijn","overwegende dat laagdrempelige hulp aantoonbaar werkt","overwegende dat vrijwilligers effectief kunnen worden ingezet","overwegende dat financiering beperkt maar doelmatig kan zijn","overwegende dat monitoring op bereik nodig is"]','["dat digitale hulp structureel wordt geborgd","dat het loket en bibliotheek extra spreekuren bieden","dat verwijzing via wijkteams en huisartsen wordt versterkt","dat resultaten halfjaarlijks worden gemeten","dat successen en knelpunten publiek worden gedeeld"]','verworpen','2022-07-05 13:05:00','2022-07-05 13:05:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (9,'Motie: Fietsenstalling met sociale toezichtfunctie Stationsplein','["dat de huidige fietsenstalling vaak vol is","dat foutgeparkeerde fietsen hinder geven","dat er onveiligheidsgevoelens zijn in de avond","dat sociaal beheer beperkt aanwezig is","dat de NS bereid is tot overleg"]','["overwegende dat voldoende capaciteit foutparkeren voorkomt","overwegende dat toezicht vandalisme en diefstal reduceert","overwegende dat samenwerking met NS en politie nodig is","overwegende dat tariefstelling de bezetting stuurt","overwegende dat nachtopenstelling onderzocht kan worden"]','["met NS te komen tot uitbreiding met 500 plekken","een beheerconcept met sociale hosts te ontwikkelen","heldere bewegwijzering naar stalling aan te brengen","een proef met nachtopenstelling te verkennen","de raad voor de zomer te informeren"]','concept','2022-09-29 18:40:00','2022-09-29 18:40:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (10,'Motie: Energiearmoede – gerichte isolatieaanpak particuliere huur','["dat energieprijzen nog steeds hoog zijn voor lage inkomens","dat slecht geïsoleerde woningen veel in particuliere huur voorkomen","dat huurders beperkte invloed hebben op investeringen","dat bestaande regelingen versnipperd zijn","dat corporaties al stappen zetten, maar dit segment achterblijft"]','["overwegende dat gericht isoleren woonlasten verlaagt","overwegende dat ontzorging van particuliere verhuurders nodig is","overwegende dat koppeling met wijkgerichte aanpak efficiënt is","overwegende dat toezicht op minimale kwaliteit nodig is","overwegende dat cofinanciering mogelijk is vanuit Rijk"]','["dat energiearmoede topprioriteit is","dat een ontzorgingsloket voor particuliere verhuurders wordt ingericht","dat wijkgewijs wordt geïnventariseerd en benaderd","dat resultaten en aantallen jaarlijks worden gerapporteerd","dat misstanden actief worden aangepakt"]','ingediend','2022-11-14 10:25:00','2022-11-14 10:25:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (11,'Motie: Veilige schoolomgeving Delftwijk – 30 km-zone compleet','["dat rondom scholen nog 50 km/u stukken aanwezig zijn","dat overstekende kinderen onveiligheid ervaren","dat ouders en schoolteams om maatregelen vragen","dat fysieke drempels ontbreken op enkele routes","dat wijkagent knelpunten heeft gesignaleerd"]','["overwegende dat 30 km/u de norm moet zijn bij scholen","overwegende dat fysieke inrichting helpt bij naleving","overwegende dat inzet van verkeersbrigadiers tijdelijk kan helpen","overwegende dat handhaving selectief moet worden ingezet","overwegende dat communicatie met ouders en buurt belangrijk is"]','["alle schoolroutes tot 30 km/u te maken","fysieke drempels en plateaus aan te leggen waar nodig","tijdelijke brigadiers in te zetten tijdens de overgang","een communicatiepakket voor scholen op te leveren","na vier maanden te evalueren"]','aangenomen','2023-01-09 09:00:00','2023-01-09 09:00:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (12,'Motie: Cultuurdeelname voor jongeren met HaarlemPas','["dat deelname aan cultuur kansenongelijkheid verkleint","dat kosten drempels opwerpen voor gezinnen met lage inkomens","dat aanbieders bereid zijn tot reductie","dat bestaande regelingen niet altijd bekend zijn","dat scholen cultuurpartners zoeken"]','["overwegende dat drempelverlaging deelname vergroot","overwegende dat bundeling van regelingen overzicht biedt","overwegende dat communicatie via scholen het bereik vergroot","overwegende dat monitoring per wijk inzicht geeft","overwegende dat samenwerking met cultuurhuis essentieel is"]','["dat een cultuurvoucher voor jongeren met HaarlemPas wordt ingericht","dat aanbieders worden verbonden via één platform","dat scholen helpen bij toeleiding","dat het bereik per kwartaal wordt gemeten","dat de raad jaarlijks wordt geïnformeerd"]','Concept','2023-02-27 15:35:00','2023-02-27 15:35:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (13,'Motie: Herstel verzakte kademuren Prinsen Bolwerk versnellen','["dat meerdere kademuren in slechte staat verkeren","dat uitstel risico’s en kosten vergroot","dat omwonenden zorgen uiten over veiligheid","dat werk met werk maken hinder kan beperken","dat subsidies beschikbaar zijn via provincie"]','["overwegende dat veiligheid prioriteit heeft","overwegende dat fasering en planning hinder beperken","overwegende dat communicatie cruciaal is","overwegende dat budgetzekerheid nodig is","overwegende dat toezicht en kwaliteitsborging vereist zijn"]','["een versneld uitvoeringsscenario uit te werken","provinciale cofinanciering te claimen","omwonenden via een bouwapp te informeren","hinderbeperkende maatregelen te treffen","de raad bij voortgang te betrekken"]','Concept','2023-04-17 12:50:00','2023-04-17 12:50:00',1,'Draagt het college op nu','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (14,'Motie: Transparantie lobbycontacten bij grote projecten','["dat grote projecten veel belangen kennen","dat transparantie publieke waarde vergroot","dat overzicht van lobbycontacten nu versnipperd is","dat andere gemeenten al registers hanteren","dat het vertrouwen in de overheid onder druk staat"]','["overwegende dat inzicht in contacten integriteit ondersteunt","overwegende dat privacy en proportionaliteit bewaakt moeten worden","overwegende dat werkbaarheid voor ambtenaren belangrijk is","overwegende dat digitale tooling beschikbaar is","overwegende dat fasering mogelijk is"]','["dat een openbaar register voor lobbycontacten wenselijk is","dat pilots starten bij drie grote projecten","dat spelregels en definities helder worden vastgelegd","dat privacy wordt geborgd volgens AVG","dat de raad halfjaarlijks een evaluatie ontvangt"]','ingediend','2023-06-06 20:10:00','2023-06-06 20:10:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (15,'Motie: Snellere verwerking vergunningen voor evenementen in parken','["dat organisatoren lange doorlooptijden ervaren","dat parken belangrijke ontmoetingsplekken zijn","dat omwonenden behoefte hebben aan voorspelbare kaders","dat afstemming tussen diensten complex is","dat er piekdruk is in het voorjaar"]','["overwegende dat een loketfunctie doorlooptijd verkort","overwegende dat heldere kaders conflicten voorkomen","overwegende dat planning en spreiding over seizoenen kan helpen","overwegende dat digitale aanvraag ondersteuning biedt","overwegende dat evaluatie na het seizoen nodig is"]','["één evenementenloket voor parken te organiseren","jaarkalenders met bandbreedtes te publiceren","een spoedprocedure voor kleine evenementen te introduceren","omwonenden vooraf te informeren via wijkkanalen","na het seizoen de doorlooptijden te rapporteren"]','aangenomen','2023-07-19 08:45:00','2023-07-19 08:45:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (16,'Motie: Verkeersveiligheid Zijlweg – vrachtverkeer omleiden','["dat zwaar verkeer door de Zijlweg rijdt","dat trillingen schade veroorzaken aan panden","dat fietsers en voetgangers zich onveilig voelen","dat alternatieve routes beschikbaar zijn","dat ondernemers logistieke belangen hebben"]','["overwegende dat leefbaarheid en veiligheid leidend zijn","overwegende dat omleiding piekbelasting vermindert","overwegende dat uitzonderingen nodig zijn voor bevoorrading","overwegende dat handhaving en communicatie essentieel zijn","overwegende dat evaluatie op bereikbaarheid nodig is"]','["dat zwaar verkeer tijdens spits wordt omgeleid","dat uitzonderingsvensters voor bevoorrading gelden","dat handhaving plaatsvindt op piekmomenten","dat bewoners en ondernemers worden meegenomen","dat de maatregel na 6 maanden wordt geëvalueerd"]','verworpen','2023-09-04 17:05:00','2023-09-04 17:05:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (17,'Motie: Lokale aanpak schulden – vroegsignalering opschalen','["dat schulden problematiek leidt tot stress en zorgmijding","dat vroegsignalering effectief is maar nog niet dekkend","dat gegevensdeling juridisch kan binnen kaders","dat partners zoals woningcorporaties willen meedoen","dat energiebedrijven signalen kunnen aanleveren"]','["overwegende dat voorkomen beter is dan genezen","overwegende dat privacy-by-design noodzakelijk is","overwegende dat outreachende hulp werkt","overwegende dat integrale samenwerking rendement vergroot","overwegende dat structurele monitoring nodig is"]','["vroegsignalering met alle relevante partners te sluiten","privacyprotocollen te actualiseren en te delen","extra schuldhulpverleners in te zetten in piekperioden","resultaten per kwartaal te monitoren","de raad jaarlijks te informeren over effecten"]','concept','2023-10-23 11:30:00','2023-10-23 11:30:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (18,'Motie: Speelruimteplan met schaduw in elk stadsdeel','["dat speelplekken in de zomer te heet worden","dat ongelijkheid bestaat in kwaliteit per wijk","dat ouders behoefte hebben aan schaduw en water","dat bestaande toestellen verouderd zijn","dat jongerenparticipatie kansen biedt"]','["overwegende dat buitenspelen gezondheid bevordert","overwegende dat schaduwrijke plekken hitte tegengaan","overwegende dat ontwerp met kinderen betere keuzes oplevert","overwegende dat onderhoud geclusterd kan worden","overwegende dat fasering over meerdere jaren mogelijk is"]','["dat elk stadsdeel een schaduwrijke speelplek krijgt","dat kinderen en jongeren actief meedenken","dat onderhoud en vervanging worden gekoppeld","dat resultaten en planning publiek worden gedeeld","dat jaarlijks wordt gerapporteerd over voortgang"]','ingediend','2023-12-12 14:00:00','2023-12-12 14:00:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (20,'Motie: Veilige fietsroutes naar sportparken','["dat veel jeugd op de fiets naar sport gaat","dat kruisingen op routes onoverzichtelijk zijn","dat trainers ongevallen melden","dat verlichting op enkele stukken ontbreekt","dat clubs willen meewerken aan bewustwording"]','["overwegende dat veilige routes sportdeelname stimuleert","overwegende dat simpele ingrepen snel uitvoerbaar zijn","overwegende dat verlichting sociale veiligheid vergroot","overwegende dat samenwerking met clubs draagvlak vergroot","overwegende dat evaluatie tijdens competitieseizoen logisch is"]','["dat prioritaire knelpunten binnen drie maanden worden aangepakt","dat ontbrekende verlichting wordt toegevoegd","dat clubs verkeerslessen onder de aandacht brengen","dat meldingen actief worden opgepakt","dat de raad een voortgangsoverzicht ontvangt"]','Concept','2024-03-18 16:55:00','2025-09-20 08:08:22.829400',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (21,'Motie: Beschermen van betaalbare werkruimtes voor makers','["dat creatieve makers werkruimtes verliezen door stijgende huren","dat leegstand bestaat in enkele gemeentelijke panden","dat broedplaatsen economische en sociale waarde hebben","dat doorstroming stokt zonder aanbod","dat samenwerking met vastgoedpartijen mogelijk is"]','["overwegende dat betaalbare ruimte cruciaal is voor makers","overwegende dat tijdelijk gebruik leegstand vermindert","overwegende dat duidelijke selectiecriteria nodig zijn","overwegende dat beheer en veiligheid geborgd moeten worden","overwegende dat transparante toewijzing het vertrouwen vergroot"]','["een broedplaatsen-programma met selectiecriteria op te stellen","leegstaande panden te inventariseren en tijdelijk in te zetten","een beheerpartner te selecteren","de toewijzing openbaar te publiceren","jaarlijks impact te rapporteren"]','Concept','2024-05-07 13:20:00','2025-09-19 18:02:06.039857',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (22,'Motie: Waterbesparing gemeentelijke sportaccommodaties','["dat drinkwatergebruik in sportaccommodaties hoog is","dat waterdruk en piekmomenten verspilling veroorzaken","dat slimme douches water kunnen besparen","dat sportverenigingen willen meedenken","dat eerdere pilots positief uitvielen"]','["overwegende dat duurzaamheid en kosten samen kunnen gaan","overwegende dat gedrag en techniek beide nodig zijn","overwegende dat monitoring inzicht geeft in besparing","overwegende dat subsidies beschikbaar zijn","overwegende dat schaalgrootte inkoopvoordeel geeft"]','["dat slimme waterbesparende systemen worden geplaatst","dat met verenigingen afspraken worden gemaakt","dat besparingen inzichtelijk worden gemaakt op een dashboard","dat resultaten worden gedeeld met andere gemeenten","dat na 12 maanden een evaluatie volgt"]','ingediend','2024-06-24 19:15:00','2024-06-24 19:15:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (23,'Motie: Herstel biodiversiteit langs de Haarlemse singels','["dat biodiversiteit onder druk staat","dat oevers kansen bieden voor natuurvriendelijke inrichting","dat maaibeheer invloed heeft op soortenrijkdom","dat vrijwilligersgroepen actief willen bijdragen","dat educatie mogelijkheden biedt"]','["overwegende dat natuurvriendelijke oevers waterkwaliteit verbeteren","overwegende dat aangepast maaibeheer soorten helpt","overwegende dat participatie eigenaarschap vergroot","overwegende dat koppeling met onderwijs meerwaarde heeft","overwegende dat fasering over meerdere oevers wenselijk is"]','["een oeverherstelplan op te stellen met prioritering","maaibeheer ecologisch aan te passen","vrijwilligersgroepen te ondersteunen met materialen","educatieborden te plaatsen","jaarlijks de voortgang te rapporteren"]','Concept','2024-08-13 10:05:00','2025-09-19 18:02:32.346808',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (24,'Motie: Eerlijke stageplaatsen bij de gemeente Haarlem','["dat stagiairs bijdragen aan publieke dienstverlening","dat vergoedingen verschillen per afdeling","dat gelijke kansen niet altijd geborgd zijn","dat diversiteit en inclusie speerpunten zijn","dat HR een centrale rol heeft"]','["overwegende dat eerlijke vergoedingen passend zijn","overwegende dat gelijke selectieprocedures nodig zijn","overwegende dat begeleiding kwaliteit bepaalt","overwegende dat doorstroom naar banen mogelijk is","overwegende dat inzicht in cijfers noodzakelijk is"]','["dat een gemeente-brede stageverordening wordt ingevoerd","dat een minimumvergoeding wordt vastgesteld","dat selectie en begeleiding uniform worden ingericht","dat cijfers over instroom en doorstroom worden gedeeld","dat jaarlijks wordt geëvalueerd op kwaliteit"]','verworpen','2024-09-30 15:25:00','2024-09-30 15:25:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (25,'Motie: Toegankelijke speeltuinen voor kinderen met een beperking','["dat niet alle speeltuinen inclusief zijn ingericht","dat ouders knelpunten ervaren bij toegankelijkheid","dat aanpassingen vaak eenvoudig en betaalbaar zijn","dat kinderen mee willen kunnen doen in de wijk","dat onderhoudsmomenten kansen bieden"]','["overwegende dat inclusie uitgangspunt is","overwegende dat kleine ingrepen groot effect hebben","overwegende dat participatie met ouders noodzakelijk is","overwegende dat beheer daarop moet worden ingericht","overwegende dat monitoring op gebruik zinvol is"]','["per stadsdeel minstens één inclusieve speeltuin te realiseren","ouders en kinderen te betrekken bij ontwerp","toegankelijkheid op te nemen in beheerplannen","een overzichtskaart te publiceren","na een jaar gebruik te evalueren"]','Concept','2024-11-18 09:15:00','2025-09-20 08:08:34.773417',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (27,'Motie: Verbeteren doorstroming buslijnen op de Westelijke Randweg','["dat bussen vastlopen in spitsverkeer","dat reizigers langere reistijd ervaren","dat doorstroming OV belangrijk is voor aantrekkelijkheid","dat kruispunten knelpunten vormen","dat provinciale afstemming nodig is"]','["overwegende dat OV-prioriteit uitstoot vermindert","overwegende dat slimme VRI’s de doorstroming verbeteren","overwegende dat kleine infrastructurele ingrepen helpen","overwegende dat communicatie met weggebruikers nodig is","overwegende dat monitoring met data de effecten toont"]','["met provincie en vervoerder OV-prioriteit uit te breiden","slimme VRI-instellingen te optimaliseren","knelpuntkruispunten gericht aan te pakken","reizigers te informeren over verbeteringen","na 6 maanden te rapporteren over reistijdwinst"]','aangenomen','2025-03-03 14:45:00','2025-03-03 14:45:00',1,'Draagt het college op','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (28,'Motie: Aanpak eenzaamheid – ontmoetingsplekken in elke wijk','["dat eenzaamheid toeneemt onder jongeren en ouderen","dat ontmoetingsplekken ongelijk verdeeld zijn","dat vrijwilligersorganisaties aanbod willen uitbreiden","dat signalering vanuit zorg en welzijn toeneemt","dat kleine bijdragen grote impact kunnen hebben"]','["overwegende dat laagdrempelige plekken drempels verlagen","overwegende dat samenwerking in de wijk essentieel is","overwegende dat vindbaarheid versterkt moet worden","overwegende dat monitoring op bereik en effect nodig is","overwegende dat structurele borging gewenst is"]','["dat in elke wijk een ontmoetingsplek beschikbaar is","dat coördinatie bij het wijkteam wordt belegd","dat vrijwilligers worden ondersteund","dat informatie centraal wordt ontsloten","dat jaarlijks impact wordt gemeten"]','verworpen','2025-04-22 11:10:00','2025-04-22 11:10:00',1,'De raad spreekt uit dat','Gemeenteraad 6 november 2025','3');
INSERT INTO "motie" VALUES (30,'Motie: Schone lucht rond basisscholen – geen motor aan bij halen/brengen','["dat draaiende motoren bij scholen luchtkwaliteit verslechteren", "dat ouders vaak onbewust de motor laten lopen", "dat kinderen extra gevoelig zijn voor uitstoot", "dat communicatie op schoolpleinen beperkt is", "dat handhaving nauwelijks plaatsvindt", "zdsfsdfgdsfg", "asdgfsdfgsdfgsdfgsdfg"]','["overwegende dat schone lucht essentieel is voor gezondheid","overwegende dat gedragsbeïnvloeding effectief kan zijn","overwegende dat duidelijke borden en nudges helpen","overwegende dat toezicht tijdens piekmomenten nodig is","overwegende dat scholen partners zijn in de uitvoering"]','["dat een \u2018motor uit bij school\u2019-campagne wordt gestart", "dat borden en straatmarkeringen worden geplaatst", "dat hosts toezicht houden in de eerste weken", "dat scholen en ouders actief worden betrokken", "dat na twee maanden resultaten worden teruggekoppeld", "Nu langskomen!"]','GereedAdvies','2025-09-10 09:50:00','2025-09-21 12:38:52.312415',1,'De raad spreekt uit dat','Gemeenteraad 27 september 2025','3');
